import{_ as t,c as a,o as s,j as e,a as o}from"./chunks/framework.DQPzzBi7.js";const I=JSON.parse('{"title":"2025","description":"","frontmatter":{},"headers":[],"relativePath":"docs/Essay/2025.md","filePath":"docs/Essay/2025.md","lastUpdated":1766586472000}'),c={name:"docs/Essay/2025.md"},n=e("h1",{id:"_2025",tabindex:"-1"},[o("2025 "),e("a",{class:"header-anchor",href:"#_2025","aria-label":'Permalink to "2025"'},"​")],-1),r=e("p",null,"又一个写代码的年头快结束了，今年是我认真写代码的第一坤年。2025是一个疯狂的AI元年，各种Agent层出不穷，各种AI模型让普通人都触手可得。在享受到ai带来的红利的同时，也给我带来了危机感，我并非指替代危机而是一种软件危机，近大半年里我管理的项目团队共吸纳了三位后端新同事，日常开发中我们受益于模型的代码预测能少敲几下键盘鼠标，延缓腱鞘炎，也能降低索引有效内容的成本，将部分注意力交给AI，或是使用cursor等AI IDE完成还不错的开发工作甚至有的方面做得比人为还好。不过这好像会让人掉入一个陷阱，就是将完成一个问题的“简单（结构清晰业务清晰）”和“容易（实现成本低）”混回一谈，编程的尽头是哲学往往复杂的业务都能抽象成简单的几个点来解决从而转化为较为简洁明了的代码来实现，其中思考的过程我认为不应该完全外包给Agent来做，因为这会造成一种现象“在项目中交付我们并不完全理解的代码”这无论是对于业务后续的扩展还是项目本身的维护来说都是不友好的，况且这种问题一旦陷入便可能会在一次次的代码生成中无限循环累加成长为软件危机，让项目不那么可控，让复杂度爆炸积累，尽管功能是实现了，可能到最后需要去修改某一部分的时候你需要思考的不是如何“设计”，而是被迫的去理解和管理复杂的上下文逻辑。或许在编程中AI真正需要的不是更好更准确的提示词，而是对项目的透彻研究，然后再与我们一同思考详细的实现计划，最后精准执行计划，并且帮助我们或者说我们在此同时能守住“理解系统”的核心能力，依然拥有项目的掌控权。所以一个程序员做好软件项目的本质难题不是敲键盘，而是知道该敲什么。一点拙见",-1),d=e("p",null,"ps：牙齿有问题早治疗早享受，最近做根管，不仅要花几千大洋，还特别麻烦，还得后期保护得好才能管十年",-1),_=[n,r,d];function i(l,p,h,m,f,u){return s(),a("div",null,_)}const x=t(c,[["render",i]]);export{I as __pageData,x as default};
